void chi2(int &npar, double *gin, double &f, double *par, int iflag) {
	//calculate chisquare
	double chisq = 0;
	// loop over all layers and vertex position (36 layers + 1 vertex)
	for (int j = 0; j < 8; ++j) { 
		for (int i = 0; i < 35; ++i) {
			if ((j>6)&&(i==36)){
				continue; // don't constrain the vertices of the tracks theta = 
				// [15,20] degree tracks (too hard to fit peak)
			}
			chisq += calculate_residual(cur_sector, j, i, par);
		}
	}
	f = chisq; 
}

